import React, { useState } from 'react';
import api from '../services/api';

export default function Chatbot() {
  const [messages, setMessages] = useState([
    { from: 'bot', text: "Welcome! I'm NGO Connect Bot. Ask me about registration, NGO recommendations, or how to use the platform!" }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  const send = async e => {
    e.preventDefault();
    if (!input.trim()) return;

    setMessages([...messages, { from: 'user', text: input }]);
    setLoading(true);

    try {
      const res = await api.post('/ai/chat', { message: input });
      setMessages(msgs => [...msgs, { from: 'bot', text: res.data.reply }]);
    } catch (err) {
      setMessages(msgs => [...msgs, { from: 'bot', text: 'Sorry, I encountered an error. Please try again.' }]);
    }

    setLoading(false);
    setInput('');
  };

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">NGO Connect Chatbot ðŸ¤–</h2>
      <div className="mt-4 space-y-3 h-80 overflow-y-auto border p-4 bg-gray-50 rounded">
        {messages.map((m, i) => (
          <div key={i} className={`flex ${m.from === 'bot' ? 'justify-start' : 'justify-end'}`}>
            <div className={`max-w-xs p-3 rounded ${m.from === 'bot' ? 'bg-blue-100 text-blue-900' : 'bg-green-100 text-green-900'}`}>
              <p className="text-sm">{m.text}</p>
            </div>
          </div>
        ))}
      </div>
      <form onSubmit={send} className="mt-4 flex gap-2">
        <input 
          value={input} 
          onChange={e=>setInput(e.target.value)} 
          disabled={loading} 
          className="flex-1 p-3 border rounded disabled:bg-gray-100" 
          placeholder="Type your question..." 
        />
        <button 
          disabled={loading} 
          className="px-6 py-3 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:bg-gray-400 font-semibold"
        >
          {loading ? 'Sending...' : 'Send'}
        </button>
      </form>
    </div>
  );
}
