import React, { useEffect, useState } from 'react';
import api from '../services/api';
import { Link } from 'react-router-dom';

export default function NgoList(){
  const [ngos, setNgos] = useState([]);
  useEffect(()=>{ api.get('/ngos').then(r=>setNgos(r.data)).catch(()=>{}); },[]);
  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-2xl font-bold">Verified NGOs</h1>
      <div className="mt-6 grid grid-cols-1 gap-4">
        {ngos.map(n => (
          <div key={n._id} className="p-4 bg-white rounded shadow">
            <h2 className="font-bold">{n.name}</h2>
            <p className="text-sm">{n.category} â€¢ {n.location}</p>
            <Link to={`/ngos/${n._id}`} className="text-blue-600">View</Link>
          </div>
        ))}
      </div>
    </div>
  );
}
