import React, { useEffect, useState } from 'react';
import api from '../services/api';
import { Link } from 'react-router-dom';

export default function NgoList(){
  const [ngos, setNgos] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('');
  const [location, setLocation] = useState('');

  useEffect(()=>{
    api.get('/ngos').then(r=>setNgos(r.data)).catch(()=>{});
  },[]);

  const filteredNgos = ngos.filter(ngo => {
    return (
      ngo.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (category === '' || ngo.category === category) &&
      (location === '' || ngo.location === location)
    );
  });

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-2xl font-bold">Verified NGOs</h1>
      <div className="mt-6 flex space-x-4">
        <input
          type="text"
          placeholder="Search by name"
          className="p-2 border rounded w-full"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
        <input
          type="text"
          placeholder="Filter by category"
          className="p-2 border rounded w-full"
          value={category}
          onChange={e => setCategory(e.target.value)}
        />
        <input
          type="text"
          placeholder="Filter by location"
          className="p-2 border rounded w-full"
          value={location}
          onChange={e => setLocation(e.target.value)}
        />
      </div>
      <div className="mt-6 grid grid-cols-1 gap-4">
        {filteredNgos.map(n => (
          <div key={n._id} className="p-4 bg-white rounded shadow">
            <h2 className="font-bold">{n.name}</h2>
            <p className="text-sm">{n.category} â€¢ {n.location}</p>
            <Link to={`/ngos/${n._id}`} className="text-blue-600">View</Link>
          </div>
        ))}
      </div>
    </div>
  );
}
