import React, { useState } from 'react';
import api from '../services/api';

export default function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  const send = async e => {
    e.preventDefault();
    if (!input.trim()) return;

    setMessages([...messages, { from: 'user', text: input }]);
    setLoading(true);

    try {
      const res = await api.post('/ai/chat', { message: input });
      setMessages(msgs => [...msgs, { from: 'bot', text: res.data.reply }]);
    } catch (err) {
      setMessages(msgs => [...msgs, { from: 'bot', text: 'Sorry, I encountered an error. Please try again.' }]);
    }

    setLoading(false);
    setInput('');
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded shadow">
      <h2 className="text-xl font-bold">NGO Connect Chatbot</h2>
      <div className="mt-4 space-y-2 h-48 overflow-y-auto border p-2 bg-gray-50">
        {messages.map((m, i) => (
          <div key={i} className={m.from === 'bot' ? 'text-blue-600' : 'text-gray-800'}>
            <b>{m.from === 'bot' ? 'Bot' : 'You'}:</b> {m.text}
          </div>
        ))}
      </div>
      <form onSubmit={send} className="mt-4 flex gap-2">
        <input value={input} onChange={e=>setInput(e.target.value)} className="flex-1 p-2 border rounded" placeholder="Ask me anything..." />
        <button className="px-4 py-2 bg-blue-600 text-white rounded">Send</button>
      </form>
    </div>
  );
}
