import React, { useEffect, useState } from 'react';
import api from '../services/api';
import { Link } from 'react-router-dom';

export default function CampaignList() {
  const [campaigns, setCampaigns] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('');
  const [location, setLocation] = useState('');

  useEffect(() => {
    api.get('/campaigns').then(r => setCampaigns(r.data));
  }, []);

  const filteredCampaigns = campaigns.filter(c => {
    return (
      c.title.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (category === '' || c.category === category) &&
      (location === '' || c.location === location)
    );
  });

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-2xl font-bold">Campaigns</h1>
      <div className="mt-6 flex space-x-4">
        <input
          type="text"
          placeholder="Search by title"
          className="p-2 border rounded w-full"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
        <input
          type="text"
          placeholder="Filter by category"
          className="p-2 border rounded w-full"
          value={category}
          onChange={e => setCategory(e.target.value)}
        />
        <input
          type="text"
          placeholder="Filter by location"
          className="p-2 border rounded w-full"
          value={location}
          onChange={e => setLocation(e.target.value)}
        />
      </div>
      <div className="mt-6 grid grid-cols-1 gap-4">
        {filteredCampaigns.map(c => (
          <div key={c._id} className="p-4 bg-white rounded shadow">
            <h2 className="font-bold">{c.title}</h2>
            <p>{c.description}</p>
            <p className="text-sm">{c.category} â€¢ {c.location}</p>
            <p className="text-xs">By: {c.ngo?.name}</p>
            <Link to={`/campaigns/${c._id}`} className="text-blue-600">View</Link>
          </div>
        ))}
      </div>
    </div>
  );
}
